function socialShare(){const e=document.querySelectorAll('[data-share="link"]');e.forEach((e=>{e.addEventListener("click",(t=>{t.preventDefault(),(async e=>{const t=window.location.href,o=e.querySelector('[data-share="copy"]'),a=e.querySelector('[data-share="copied"]'),s=e.querySelector('[data-share="tooltip"]');try{await navigator.clipboard.writeText(t),o&&a&&(a.style.display="block",setTimeout((()=>{a.classList.add("is-open")}),10)),s&&(s.style.display="block",setTimeout((()=>{s.classList.add("is-open")}),10)),setTimeout((()=>{o&&a&&(a.classList.remove("is-open"),s.classList.remove("is-open"),setTimeout((()=>{a.style.display="none",s.style.display="none"}),300))}),2e3)}catch(e){console.error("Failed to copy URL:",e)}})(e)}))}))}window.addEventListener("load",(()=>{socialShare()}));