function founderCarousel(){const e=document.querySelector(".founders_wrap");if(!e)return;const r=e.querySelector(".founders_cms.swiper"),o=r.querySelector(".founders_list.swiper-wrapper"),t=e.querySelector(".slider_arrow.swiper-prev"),n=e.querySelector(".slider_arrow.swiper-next"),s=Array.from(o.querySelectorAll(".swiper-slide"));const a=function(e,r=6){const o=new Set,t=[],n=[...e].sort((()=>Math.random()-.5));for(const e of n){const n=e.querySelector('[data-element="company"]');if(!n)continue;const s=n.textContent.trim().toLowerCase();if(!o.has(s)&&(o.add(s),t.push(e),t.length>=r))break}return t}(s,6);s.forEach((e=>e.remove())),a.forEach((e=>{o.appendChild(e)})),setTimeout((()=>{new Swiper(r,{slidesPerView:"auto",spaceBetween:12,speed:500,loop:!0,watchOverflow:!0,autoplay:{delay:3e3,pauseOnMouseEnter:!0},navigation:{nextEl:n,prevEl:t},breakpoints:{992:{spaceBetween:16}}})}),100)}window.addEventListener("load",(()=>{founderCarousel()}));